use ringbuffer::{AllocRingBuffer, RingBufferExt, RingBufferWrite};

fn main() {
    let input = "";

    // Part 1
    debug_assert!(find_header(String::from("mjqjpqmgbljsphdztnvjfqwrcgsmlb"), 4).unwrap() == 7);
    debug_assert!(find_header(String::from("bvwbjplbgvbhsrlpgdmjqwftvncz"), 4).unwrap() == 5);
    debug_assert!(find_header(String::from("nppdvjthqldpwncqszvftbrmjlhg"), 4).unwrap() == 6);
    debug_assert!(find_header(String::from("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg"), 4).unwrap() == 10);
    debug_assert!(find_header(String::from("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw"), 4).unwrap() == 11);
    println!(
        "Part1: {}",
        find_header(String::from(input), 4).unwrap_or_default()
    );

    // Part 2
    debug_assert!(find_header(String::from("mjqjpqmgbljsphdztnvjfqwrcgsmlb"), 14).unwrap() == 19);
    debug_assert!(find_header(String::from("bvwbjplbgvbhsrlpgdmjqwftvncz"), 14).unwrap() == 23);
    debug_assert!(find_header(String::from("nppdvjthqldpwncqszvftbrmjlhg"), 14).unwrap() == 23);
    debug_assert!(
        find_header(String::from("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg"), 14).unwrap() == 29
    );
    debug_assert!(find_header(String::from("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw"), 14).unwrap() == 26);
    println!(
        "Part2: {}",
        find_header(String::from(input), 14).unwrap_or_default()
    );
}

fn find_header(s: String, length: usize) -> Option<usize> {
    // RingBuffer can only be powers of 2 sized...
    let bufsz = length.next_power_of_two();
    let ignore_start = bufsz - length;
    // println!("Using buffer size {} for request {}, ignoring {}",bufsz,length, ignore_start);

    let mut buffer: AllocRingBuffer<char> = AllocRingBuffer::with_capacity(bufsz);

    for (i, c) in s.chars().enumerate() {
        buffer.push(c);
        let v = buffer.to_vec();
        if v.len() == bufsz {
            let mut v2 = Vec::from(&v[ignore_start..]);
            v2.sort();
            v2.dedup();
            if v2.len() == length {
                return Some(i + 1);
            }
        }
    }
    None
}
